mapper.insert:
insert into work_reply(
reply_file_name,
reply_file_path,
reply_file_size
)
values(
#{replyFileName, jdbcType=VARCHAR},
#{replyFilePath, jdbcType=VARCHAR},
#{replyFileSize, jdbcType=INTEGER}
